import React, {useState} from 'react'

export default function BountyForm(props) {
    const initInputs = {firstName: props.firstName || "", lastName: props.lastName || "", bountyAmount: props.bountyAmount|| "", type: props.type || ""}
    const [inputs, setInputs] = useState(initInputs)

    function handleChange(e){
        const {name, value, checked} = e.target
        setInputs(prevInputs => ({...prevInputs, [name]: value} && { [name]: checked }))
    }

    function handleSubmit(e){
        e.preventDefault()
        props.submit(inputs, props._id)
        setInputs(initInputs)
    }

    console.log(inputs)

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input 
                type="text"
                name="firstName"
                value={inputs.firstName}
                onChange={handleChange}
                placeholder="First Name"/>

                <input 
                type="text"
                name="lastName"
                value={inputs.lastName}
                onChange={handleChange}
                placeholder="Last Name"/>

                <input 
                type="text"
                name="bountyAmount"
                value={inputs.bountyAmount}
                onChange={handleChange}
                placeholder="bounty"/>

                {/* <input 
                type="text"
                name="type"
                value={inputs.type}
                onChange={handleChange}
                placeholder="Type"/> */}

                <label>
                    <input 
                    type="radio"
                    name="type"
                    checked={type === "jedi"}
                    value="jedi"
                    onChange={handleChange}
                    placeholder="Type"/>
                    Jedi
                </label>

                <label>
                    <input 
                    type="radio"
                    name="type"
                    value="sith"
                    onChange={handleChange}
                    placeholder="Type"/>
                    Sith
                </label>
                <button>{props.btnText}</button>
            </form>
        </div>
    )
}
