import React, {useState} from 'react'
import { use } from '../../../routes/bountyRouter'

export default function BountyForm(props) {
    const initInputs = {firstName: "", lastName: "", bounty: 0, type:""}
    const [inputs, setInputs] = useState(initInputs)

    function handleChange(e){
        const {name, value} = e.target
        setInputs(prevInputs => ({...prevInputs, [name]: value}))
    }

    return (
        <div>
            <form>
                <input 
                type="text"
                name="firstName"
                value={firstName}
                onChange={handleChange}
                placeholder="First Name"/>

                <input 
                type="text"
                name="lastName"
                value={lastName}
                onChange={handleChange}
                placeholder="Last Name"/>

                <input 
                type="number"
                name="bounty"
                value={bounty}
                onChange={handleChange}
                placeholder="bounty"/>

                <input 
                type="text"
                name="type"
                value={type}
                onChange={handleChange}
                placeholder="Type"/>
            </form>
        </div>
    )
}
